mapper.select:

select *
from(
	select A.*, rownum as rnum
	from(

		select 
			bno, title, content, memberid, regdate, viewcnt , replycnt
		from 
			qa 
		where bno > 0 
		order by bno desc, regdate desc

		) A
		where rownum <= #{page} + 10
	)
where rnum > #{page}


