mapper.update:
update qa_reply set replytext = #{replytext},
updatedate = sysdate
where rno = #{rno}

mapper.select:
select *
from(
	select A.*, rownum as rnum
	from(
		select
			*
		from
			qa_reply
		where
			bno =#{bno}
		order by rno desc
		) A
		where rownum <= #{cri.pageStart} + #{cri.perPageNum}
	)
where rnum > #{cri.pageStart}
