mapper.update:
update tbl_comment set content = #{content},
updatedate = sysdate
where cno = #{cno}

mapper.select:
SELECT *
FROM (
	SELECT ROWNUM RN, A.*
	FROM (
		SELECT *
		FROM tbl_comment
		WHERE bno = #{bno}
		ORDER BY groupno asc, grouporder asc
	) A
)
WHERE RN BETWEEN #{pagingVO.start} AND #{pagingVO.end}


SELECT NVL(MIN(grouporder), 0)
FROM tbl_comment
WHERE bno = #{bno}
AND groupno = #{groupno}
AND grouporder > #{grouporder}
AND depth <= #{depth}

SELECT NVL(MAX(grouporder), 0) + 1
FROM tbl_comment
WHERE bno = #{bno}
AND groupno = #{groupno}

mapper.update:
UPDATE tbl_comment
SET grouporder = grouporder + 1
WHERE bno = #{bno}
AND groupno = #{groupno}
AND grouporder >= #{grouporder}
