mapper.select:
select photo from recipedetail
where recipeno=#{recipeno} 
and recipedetailno =
(select max(recipedetailno) 
from recipedetail where recipeno=#{recipeno})

SELECT * FROM ( SELECT a.*, ROWNUM as rnum FROM ( SELECT R.*, photo FROM ( SELECT rd.recipeno, rd.photo FROM recipedetail rd, ( select recipeno, max(recipedetailno) as MAXIMUM from recipedetail group by recipeno) rdm WHERE rd.recipeno = rdm.recipeno AND rd.recipedetailno = rdm.MAXIMUM ) P, RECIPE R
, (
SELECT RECIPENO, COUNT(*) as cnt FROM (SELECT RI.RECIPENO, I.INGREDIENTNAME FROM RECIPEINGRE RI, INGREDIENT I WHERE I.INGREDIENTNO=RI.INGREDIENTNO
)
GROUP BY RECIPENO ORDER BY cnt desc ) I
WHERE P.RECIPENO = R.RECIPENO
AND R.RECIPENO = I.RECIPENO
ORDER BY R.RECIPENO asc
ORDER BY cnt desc
) a ) WHERE rnum >=#{scri.rowStart} and rnum <=#{scri.rowEnd}
